entV = document.getElementById('editor1').value;		if($("#cover").length>0)		{			var cover = $('input:radio[name="cover"]:checked').val(); 		}		else		{			var cover = null; 		}				//alert(contentV);		//alert(cover);		$(".pic_intro").each(function(){		    picIntros.push($(this).val());		});        var data1 = new data;		data1['ac_id'] = $("#ac_id").val();		data1['fileNames'] = new Array();		data1['fileNames'] = fileNames;		data1['picIntros'] = picIntros;		data1["acname"] = acnameV;		data1["date"] = dateV;		data1["content"] = contentV;		data1["cover"] = cover;						/* data1['fileNames'] = new Array();		data1['fileNames'] = urlencode(fileNames);		data1["acname"] = urlencode(acnameV);		data1["date"] = urlencode(dateV);		data1["content"] = urlencode(contentV);		data1["cover"] = urlencode(cover); */		//data1=urlencode(data1);				//document.writeln(fileNames.toString()); 			    $.ajax({		            type: 'GET',		            url: "../model/modify_activity.php",					data: data1,					success: function(res) {					             alert(res);								  location.reload();						    }				});					//{"files[]":fileNames,"acname":acname,"date":date,"content":content,"cover":cover});		//$("#callback").html(result.responseText);	}}function data() {    var ac_id;	var fileNames;    var acname;	var date;	var content;	var cover;	var picIntros;}</script></body></html>