insert  into `academic`(`a_id`,`a_name`,`a_type`,`a_content`,`a_date`,`a_pic_cover_path`) values (46,'漆奇博士获“中科院奖学金”','b','<br />\n<p style=\"text-indent:2em;\">\n	我课题组2006级博士生漆奇同学获得2008年度“中国科学院奖学金”，本年度，吉林大学共有2名同学获得该项社会奖学金。据悉，这是吉林大学目前研究生中奖励额度最高、获奖人数最少的社会奖学金。\n</p>\n<p style=\"text-indent:2em;\">\n	新闻来源：<a href=\"http://oa.jlu.edu.cn/show.asp?s=1&amp;id=125768\">http://oa.jlu.edu.cn/show.asp?s=1&amp;id=125768</a> \n</p>','2008-10-10','../picture/academic/null'),(47,'王书娟硕士获吉林大学“2009届优秀毕业研究生”荣誉称号','b','<p style=\"text-indent:2em;\">\n	我课题组2007级硕士生王书娟同学获得2009年度吉林大学“优秀毕业研究生”荣誉称号，本年度电子科学与工程学院共13名同学被评为吉林大学“优秀毕业研究生”。\n</p>\n<p style=\"text-indent:2em;\">\n	新闻来源：<a href=\"http://oa.jlu.edu.cn/cache/20093/show154628.html\">http://oa.jlu.edu.cn/cache/20093/show154628.html</a> \n</p>','2009-03-23','../picture/academic/null'),(48,'王蕊博士获“CASC公益奖学金”','b','<p style=\"text-indent:2em;\">\n	我课题组2007级博士生王蕊同学获得2009年度“CASC公益奖学金”一等奖。本年度吉林大学共有6名同学获得该社会奖学金，其中一等奖1名，二等奖2名，三等奖3名。\n</p>\n<p style=\"text-indent:2em;\">\n	新闻来源：<a href=\"http://oa.jlu.edu.cn/cache/200910/show160369.html\">http://oa.jlu.edu.cn/cache/200910/show160369.html</a> \n</p>','2009-10-12','../picture/academic/null'),(49,'曾毅博士获“华为奖学金”','b','<p style=\"text-indent:2em;\">\n	我课题组2007级博士生曾毅同学获得2009年度“华为奖学金” 本年度吉林大学共有6名同学获得该项社会奖学金。\n</p>\n<p style=\"text-indent:2em;\">\n	新闻来源：<a href=\"http://oa.jlu.edu.cn/show.asp?s=1&amp;id=160720\">http://oa.jlu.edu.cn/show.asp?s=1&amp;id=160720</a> \n</p>','2009-10-25','../picture/academic/null'),(50,'课题组师生在杭州参加STC’2009第十一届全国敏感元件与传感器学术会议','c','<p style=\"text-indent:2em;\">\n	2009年11月4-6日，张彤教授、2008级博士生贺媛和范会涛同学参加了在杭州浙江大学召开的STC’2009第十一届全国敏感元件与传感器学术会议，2009级硕士生徐秀娟同学的论文“LaFeO<sub>3</sub> 纳米纤维的制备及其气敏特性研究”获得大会优秀论文二等奖。\n</p>','2009-11-10','../picture/academic/null'),(51,'范会涛博士的研究论文在线出版后，近期被多次下载','a','<p style=\"text-indent:2em;\">\n	2008级博士生范会涛发表在Nanotechnology 的论文“Preparation of LaFeO3 nanofibers by electrospinning for gas sensors with fast response and recovery” 在线出版后，近期被多次下载。该刊物的编辑特发来邮件，表示祝贺。\n</p>\n<div style=\"text-indent:2em;\">\n</div>\n<div style=\"text-indent:2em;\">\n	Dear Dr Zhang,\n</div>\n<div style=\"text-indent:2em;\">\n</div>\n<div style=\"text-indent:2em;\">\n	I am pleased to tell you that your article, \"Preparation of LaFeO3 nanofibers by electrospinning for gas sensors with fast response and recovery\", in Nanotechnology, Vol 22, pp115502 (2011), has been downloaded 250 times so far.\n</div>\n<div style=\"text-indent:2em;\">\n</div>\n<div style=\"text-indent:2em;\">\n	To put this into context, across all IOP journals 10% of articles were accessed over 250 times this quarter.\n</div>\n<div style=\"text-indent:2em;\">\n	You can link directly to your article at: <a href=\"http://stacks.iop.org/0957-4484/22/115502\">http://stacks.iop.org/0957-4484/22/115502</a> \n</div>','2011-04-12','../picture/academic/null'),(52,'张彤教授参加第十三届湿度与水分学术交流会议','c','<p style=\"text-indent:2em;\">\n	2011年9月17日至21日，为期三天的第十三届湿度与水分学术交流会议在苏州召开，张彤教授参加了会议，并宣读了论文。<img src=\"/background/attachments/image/upload/20120505201421_28989.jpg\" width=\"568\" height=\"379\" alt=\"\" style=\"text-align:left;text-indent:2em;\" />\n</p>\n<p style=\"text-indent:2em;\">\n	新闻来源：<a href=\"http://www.chinashidu.com.cn/news/n.aspx?id=130\">http://www.chinashidu.com.cn/news/n.aspx?id=130</a> \n</p>','2011-09-20','../picture/academic/20110920.jpg'),(53,'三名研究生同学赴台湾参加ACCS’2011国际会议','c','<p style=\"text-indent:2em;\">\r\n	课题组2010级博士生王丽丽、2011级博士生娄正、2010级硕士生冯莹靓三名同学于2011年11月14~20日赴台湾台北市参加2011年9th Asian Conference on Chemical Sensors，会上，娄正博士在大会上宣读了题为“Acetone sensing properties of hierarchical ZnO urchinlike structures by hydrothermal process”的论文，并有幸向国际著名的气体传感器专家Prof.Yamazoe求教，与国际、国内化学传感器领域的前辈们进行了学术交流。\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/background/attachments/image/upload/20120503154808_82533.jpg\" width=\"300\" height=\"450\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"white-space:nowrap;\"> 图片1：王丽丽博士在ACCS\'2011会场</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/background/attachments/image/upload/20120503154837_66496.jpg\" width=\"400\" height=\"267\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"text-align:center;\">图片2：娄正博士在ACCS\'011分会场作报告</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/background/attachments/image/upload/20120503154856_27622.jpg\" width=\"400\" height=\"267\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"white-space:nowrap;\"><span style=\"white-space:nowrap;\">图片3:王丽丽博士与Sens. Actuators. B的亚洲主编Shimizu合影</span></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/background/attachments/image/upload/20120503154931_82422.jpg\" width=\"400\" height=\"267\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<div style=\"text-align:center;\">\r\n	图片4：王丽丽博士与国际著名的传感器学者C.C.Liu合影\r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/background/attachments/image/upload/20120503155031_77045.jpg\" width=\"400\" height=\"267\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"white-space:nowrap;\">图片5:参会的吉大学生与日本著名的传感器专家Yamazoe夫妇合影</span> \r\n</p>','2011-12-25','../picture/academic/2011122501.jpg'),(54,'刘彦彤硕士获吉林大学“2012届优秀毕业研究生”荣誉称号','b','<p style=\"text-indent:2em;\">\r\n	我课题组2010级硕士生刘彦彤同学获得2012年度“优秀毕业研究生”荣誉称号，本年度电子科学与工程学院共17名同学被评为吉林大学“优秀毕业研究生”。\r\n</p>\r\n<p style=\"text-indent:2em;\">\r\n	新闻来源：<a href=\"http://oa.jlu.edu.cn/cache/20123/show182979.html\">http://oa.jlu.edu.cn/cache/20123/show182979.html</a> \r\n</p>','2012-03-25','../picture/academic/null'),(55,'贺媛硕士的学位论文获吉林省优秀硕士论文','d','<p style=\"text-indent:2em;\">\r\n	贺媛硕士的论文《基于聚吡咯系列的新型湿敏材料的特性研究》入选2010年吉林省优秀硕士学位论文\r\n</p>\r\n<div style=\"text-indent:2em;\">\r\n	我课题组2006级硕士贺媛同学的硕士毕业论文《基于聚吡咯系列的新型湿敏材料的特性研究》被评选为2010年吉林省优秀硕士学位论文，本年度吉林大学共有28篇硕士学位论文被评选为2010年吉林省优秀硕士学位论文，这是吉林省首次举办的优秀硕士论文评选活动。\r\n</div>\r\n<div style=\"text-indent:2em;\">\r\n</div>\r\n<div style=\"text-indent:2em;\">\r\n	新闻来源：<a href=\"http://oa.jlu.edu.cn/cache/20101/show163015.html\">http://oa.jlu.edu.cn/cache/20101/show163015.html</a> \r\n</div>','2010-01-12','../picture/academic/null'),(56,'课题组师生赴韩国参加ACCS’2009国际会议','c','<p style=\"text-indent:2em;\">\r\n	2009年10-14日，张彤教授与2007级博士生王蕊同学赴韩国Daegu（大邱市）参加8th Asian Conference on Chemical Sensors，会上，王蕊博士宣读了题为“Synthesis, Characterization and Humidity Sensitive Properties of Host-Guest Mesoporous Silica Materials”的论文，并与国内外许多同行进行了交流。\r\n</p>\r\n<p style=\"text-align:center;text-indent:2em;\">\r\n	<img src=\"/background/attachments/image/upload/20120503150556_18514.jpg\" width=\"300\" height=\"400\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;text-indent:2em;\">\r\n	<span style=\"white-space:nowrap;\">图片1：王蕊博士在ACCS\'2009分会场做报告</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/background/attachments/image/upload/20120503150742_52011.jpg\" width=\"568\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;text-indent:2em;\">\r\n	<span style=\"white-space:nowrap;\">图片2：吉大参会人员与日本九州大学的学者合影</span> \r\n</p>','2009-11-20','../picture/academic/2009112001.jpg'),(58,'张彤教授被聘为“吉林大学唐敖庆特聘教授”','d','<p style=\"text-indent:2em;\">\r\n	根据学校有关文件精神，经学校人才与学术工作领导小组会议讨论通过，校长办公会议研究决定，本课题组张彤被聘为吉林大学唐敖庆特聘教授，聘任时间为2012年3月26日。\r\n</p>\r\n<div style=\"text-indent:2em;\">\r\n</div>','2012-04-20','../picture/academic/null'),(59,'吉林大学校报报道我课题组漆奇博士','b','<p style=\"text-indent:2em;\">\n	<br />\n</p>\n<h2>\n	<div style=\"text-align:center;\">\n		自强不息载厚德 天道酬勤求创新\n	</div>\n	<div style=\"text-align:center;\">\n		记2011 年全国优秀博士学位论文奖获得者漆奇\n	</div>\n</h2>\n<p>\n	<br />\n</p>\n<p style=\"text-align:center;\">\n	作者：学生记者 王毓鑫\n</p>\n<br />\n<p style=\"text-align:center;\">\n	<img src=\"/background/attachments/image/upload/20120503144322_53565.jpg\" width=\"446\" height=\"600\" alt=\"\" /> \n</p>\n<p style=\"text-indent:2em;\">\n	一走进我校先进传感技术研究室，“顶天立地”的研究理念便映入眼帘???在化学传感领域开展原始创新研究，在国际学术界占有一席之地，面向国家战略急需，进行工业、民生安全和环境领域的应用开发。\n</p>\n<p style=\"text-indent:2em;\">\n	一代代传感人正是秉承着这一信念，在传感器领域中不断创新，涌现出一批又一批优秀的人才。\n</p>\n<p style=\"text-indent:2em;\">\n	今年11月，本年度全国优秀博士学位论文入选名单揭晓，我校电子科学与工程学院微电子学与固体电子学专业博士漆奇的论文《低维纳米金属氧化物半导体敏感特性研究》荣获此项殊荣。面对自己学生所取得的优异成绩，指导老师张彤非常开心。能吃苦、反应快、思维敏捷，是张老师对漆奇的整体评价。走研究路、创前人先，正是这份勇气，让漆奇成为了传感器研究领域的佼佼者。\n</p>\n<p style=\"text-indent:2em;\">\n	《低维纳米金属氧化物半导体敏感特性研究》这一论文，较为系统的研究了金属氧化物纳米敏感材料在化学传感方面的一些性质，某些传统的敏感材料在经过低维化的改性之后，敏感特性得到了很大的改善，同时纳米材料的特异性在器件的性能上得到了体现，这其中蕴含着一些科学问题。这一研究课题为高性能的气体传感的研究和开发提供了理论依据和实验基础。在问及研究思路时，漆奇坦言，读博士伊始，张彤老师刚从国外回来，她敏锐地察觉到低维纳米材料在敏感方面的研究和应用都有着巨大的潜力，同时，这方面的研究在当时的实验组条件下是切实可行的，因此经过详细的研讨和调研，最终选择了这个比较有新意的课题。正是由于师生间的密切配合、共同探讨，在研究领域不断探索，最终作出了出色的成绩。\n</p>\n<p style=\"text-indent:2em;\">\n	漆奇十分注重团队合作，他谦虚地说：“在吉林大学期间，有幸遇到了很多值得尊重的老师与朋友，在研究过程中，老师们的指导与同学们的帮助都有着极其重要的意义，一个看似简单的成果，其实真的是团队的结果，只是我有幸作为了团队的发言人而已。”在传感研究领域，经过40多年的研究积淀，先进传感技术研究室走出了拥有自己特色的创新道路。作为第二代传感人，张彤老师多次强调前辈们在传感领域做出的巨大贡献是传感人取得如此优异成绩的根本原因，在多个方向的全面探索，在专一角度深入探讨，使先进传感技术研究室在“博”与“专”两方面均得到了长足发展。\n</p>\n<p style=\"text-indent:2em;\">\n	在攻读博士期间，漆奇刻苦拼搏，发表SCI检索论文共10篇，影响因子和为31.6，在权威杂志《Sensors and Actuators B Chemical》上连续发表9篇高质量论文。获奖论文的独到之处，一方面是论文的选题方向，具有前沿的理论意义和应用前景；另一方面工作比较系统，做到了从实验数据到理论模型的完整，能够为相关的研究提供一些参考与建议。在实际的研究过程中，也遇到了许多难题，首先是研究课题比较前沿，缺乏工作经验与积累，大部分的实验过程都需要不断地摸索改进，也经历无数的失败，这方面导师张彤老师的指导起到了决定性的作用；另一方面实验设备相对落后，当初课题组的传感器研究设备与国外先进的仪器相比还是有一定的差距，于是团队自己动手搭建了一套针对性的实验设备，保证了实验顺利的开展。这种不畏难、敢攻关的精神让所有人都为之动容，实验的点滴积累成就了研究方面的突破，也为最终取得骄人的研究成果作了良好的铺垫。\n</p>\n<p style=\"text-indent:2em;\">\n	漆奇在四川开江的农村长大，幸福的童年、宽松的成长环境促进了他身心的健康成长。正是这种积极向上的成长环境让漆奇形成了刻苦执著的性格，也使他培养出很好的研究品质。在谈到父母对他的影响时，漆奇表示，一个幸福的家庭对子女的成长举足轻重，父母以身作则更会将子女引向健康地发展道路。\n</p>\n<p style=\"text-indent:2em;\">\n	目前，漆奇已经在北京工作，主要是做物联网项目的策划与运营。他认为，传感器是物联网的基础，今后随着物联网的发展，我国将会有着巨大的需求。但是目前我国的传感器与日本、英国的产品相比，有着明显的性能差距，开发高端传感器将是今后我国相关研究及产品化的难题之一。在科研方面，他现在更希望能够将科研产品应用于实际生产，产品化市场化才是科研的最终价值。\n</p>\n<p style=\"text-indent:2em;\">\n	毕业后不久，漆奇从事了一段时间传感器设备的开发，他始终不忘老师的辛勤栽培，赠送先进传感技术研究室一台元件分析设备，希望为课题组的研究提供一些便利。张彤老师说：“漆奇在工作之后还不忘回报老师，这种行为让我们研究院的成员都非常的感动，对此也十分赞扬”。一种真情始终不忘师恩，一种回馈始终铭记于心，高度的责任心让漆奇能够更好地服务于社会、贡献于社会。\n</p>\n<p style=\"text-indent:2em;\">\n	漆奇是一名共产党员，在工作和科研中他始终秉持着共产党人的先进性。积极创新、勇于探索、乐于发现、善于思考，将理论与实践紧密结合，注重研究的实际应用，使研究方向更加明确。在工作中，他注重学科的发展，着眼大局，立足基础，以宏观的角度理清时代发展的需要，进而对自身发展做出正确的引导。\n</p>\n<p style=\"text-indent:2em;\">\n	对于本科生及研究生，他希望大家能立志高远，思考自己到底在追求什么，保持自己的理想，并为之奋斗。在研究方面，他说：“苹果砸在牛顿以外的人的头上，不见得就能想出万有引力定律。创新不是灵光一闪，而是基于大量的文献阅读和同行交流上的，多看看前人在干什么，国外在干什么，同行在干什么。”追求广泛的知识面及为自己梦想而不懈拼搏正是漆奇成长所总结的基本原则，天道酬勤的观念始终让他在研究方面刻苦而执著。\n</p>\n<p style=\"text-indent:2em;\">\n	一份坚持让梦想腾飞在成长的彼岸，一份回报让爱心在付出中感动他人，一份勇敢让生活在拼搏中呈现精彩，一份执著让创新在前人的基础上展翅飞翔。漆奇在研究中刻苦、执著、奋进、合作，在张彤老师的指导下，获得了出色的科研成绩，他的创新精神和奉献理念彰显着第三代传感人特有的品质，在不懈追求中展现着传感人的风采，为创先争优的校园文化观念作出良好的诠释。\n</p>\n<p style=\"text-indent:2em;\">\n	新闻来源：<a href=\"http://epaper.jlu.edu.cn:81/show_more.php?tkey=&amp;bkey=&amp;doc_id=4147\">http://epaper.jlu.edu.cn:81/show_more.php?tkey=&amp;bkey=&amp;doc_id=4147</a> \n</p>\n<p style=\"text-indent:2em;\">\n	<br />\n</p>','2011-12-02','../picture/academic/20111202.jpg'),(61,'漆奇博士的学位论文荣获2011年全国百篇优秀博士论文','d','<p style=\"text-indent:2em;\">\r\n	今天，教育部-国务院学位委员会公布了2011年全国优秀博士学位论文的评选结果，我课题组2009届漆奇博士的学位论文“低维纳米金属氧化物半导体敏感特性的研究”，获得“百篇”殊荣。\r\n</p>\r\n<p style=\"text-indent:2em;\">\r\n	据悉，本年度共评出全国优秀博士学位论文97篇，全国优秀博士学位论文提名论文提名奖256篇。吉林大学入选优博2篇，提名奖4篇。漆奇博士是我校微电子学与固体电子学博士点首位获百篇优秀论文作者。\r\n</p>\r\n<p style=\"text-indent:2em;\">\r\n	新闻来源：<a href=\"http://http://www.moe.edu.cn/publicfiles/business/htmlfiles/moe/moe_841/201111/126737.html\" target=\"_blank\" style=\"text-indent:24px;white-space:normal;\">http://www.moe.edu.cn/publicfiles/business/htmlfiles/moe/moe_841/201111/126737.html</a> \r\n</p>\r\n<p style=\"text-indent:2em;\">\r\n	<a href=\"http://news.jlu.edu.cn/new/?mod=article&amp;act=show&amp;id=15984\" style=\"text-indent:24px;white-space:normal;\">http://news.jlu.edu.cn/new/?mod=article&amp;act=show&amp;id=15984</a> \r\n</p>\r\n<p style=\"text-indent:2em;\">\r\n	<a href=\"http://oa.jlu.edu.cn/cache/201111/show180537.html\" style=\"text-indent:2em;\">http://oa.jlu.edu.cn/cache/201111/show180537.html</a> \r\n</p>\r\n<p style=\"text-align:center;text-indent:2em;\">\r\n	<img src=\"/background/attachments/image/upload/20120505201609_57240.jpg\" width=\"400\" height=\"295\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;text-indent:2em;\">\r\n	<img src=\"/background/attachments/image/upload/20120505201647_23751.jpg\" width=\"400\" height=\"299\" alt=\"\" /> \r\n</p>','2011-11-14','../picture/academic/2011111401.jpg'),(68,'王蕊博士获第51批中国博士后科学基金面上资助','d','<p style=\"text-indent:2em;\">\r\n	<span style=\"text-indent:2em;\">最近，我课题组王蕊博士获得第51批中国博士后科学基金面上资助。这是我课题组继费腾博士获得第50批中国博士后科学基金面上资助之后的又一项博士后基金。</span> \r\n</p>\r\n<div style=\"text-indent:2em;\">\r\n	<p>\r\n		<span style=\"text-indent:2em;\">中国博士后科学基金面上资助每年上下半年各评审一次，对于在博士后期间工作突出者还可以申报中国博士后科学基金特别资助。 </span> \r\n	</p>\r\n	<p>\r\n		<br />\r\n	</p>\r\n</div>\r\n<p style=\"text-indent:2em;\">\r\n	<br />\r\n</p>','2012-05-24',NULL),(69,'娄正博士发表在New J.Chem上的文章被选为Hot Article介绍','d','','2012-06-24',NULL);

